Organization;

[ Missing_a_FirstName ] (((SpouseTitle <> '') and (SpouseGivenName = '')) or (GivenName = '')):
Title and SpouseTitle + ' ' + FamilyName;

[ Default ]
GivenName and SpouseGivenName;

row.FullNameFormat:
[ Organization ] (Organization <> ''):
Organization;

[ Different_Last_Name ] ((SpouseFamilyName <> '') and (SpouseFamilyName <> FamilyName)):
(Title + ' ' + GivenName + ' ' + MiddleName + ' ' + FamilyName + ' ' + Suffix) and (SpouseTitle + ' ' + SpouseGivenName + ' ' + SpouseMiddleName + ' ' + SpouseFamilyName);

[ Missing_Spouse_Title ] ((SpouseGivenName <> '') and (SpouseTitle = '')):
(GivenName + ' ' + MiddleName) and (SpouseGivenName + ' ' + SpouseMiddleName) + ' ' + FamilyName;

[ Default ]
Title and SpouseTitle + ' ' + GivenName + ' ' + MiddleName + ' ' + FamilyName + ' ' + Suffix;

row.AddressFormat:
[ If_No_City_Or_StateProvince ] (City = '') or (StateProvince = '') :
